import React, { useEffect, useState } from "react";

function AdminPanel() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/get/users");
        const data = await res.json();
        console.log("hello", data);
        setUsers(data);
      } catch (error) {
        console.error("Error in fetching users: ", error);
      }
    };
    fetchUsers();
  }, []);


  const handleChange = (e, id, field) => {
    const updatedUsers = users.map((user) => {
      if (user.id === id) {
        return { ...user, [field]: e.target.value };
      }
      return user;
    });
    setUsers(updatedUsers);
  };

  const handleUpdate = (user) => {
    fetch(`/api/users/${user.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(user),
    }).then(() => alert("User updated"));
  };

  return (
    <div>
      <h2>Admin Panel</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
  {users.map((user) => (
    <tr key={user.Id}>
      <td>
        <input
          value={user.username}
          onChange={(e) => handleChange(e, user.Id, "username")}
        />
      </td>
      <td>
        <input
          value={user.email}
          onChange={(e) => handleChange(e, user.Id, "email")}
        />
      </td>
      <td>
        <input
          value={user.role}
          onChange={(e) => handleChange(e, user.Id, "role")}
        />
      </td>
      <td>
        <input
          value={user.status || ""}
          onChange={(e) => handleChange(e, user.Id, "status")}
        />
      </td>
      <td>
        <button onClick={() => handleUpdate(user)}>Update</button>
      </td>
    </tr>
  ))}
</tbody>

      </table>
    </div>
  );
}

export default AdminPanel;
