import React, { useEffect, useState } from 'react';

function AdminPanel() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    cons
  }, [])

  const handleChange = (e, id, field) => {
    const updatedUsers = users.map(user => {
      if (user.id === id) {
        return { ...user, [field]: e.target.value };
      }
      return user;
    });
    setUsers(updatedUsers);
  };

  const handleUpdate = (user) => {
    fetch(`/api/users/${user.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(user)
    }).then(() => alert('User updated'));
  };

  return (
    <div>
      <h2>Admin Panel</h2>
      <table>
        <thead>
          <tr><th>Name</th><th>Email</th><th>Role</th><th>Status</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td><input value={user.name} onChange={e => handleChange(e, user.id, 'name')} /></td>
              <td><input value={user.email} onChange={e => handleChange(e, user.id, 'email')} /></td>
              <td><input value={user.role} onChange={e => handleChange(e, user.id, 'role')} /></td>
              <td><input value={user.status} onChange={e => handleChange(e, user.id, 'status')} /></td>
              <td><button onClick={() => handleUpdate(user)}>Update</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminPanel;
